(30, 'habit-moment-survey', 'marketing', 'en_US', 'We\'re all ears! Share your experience so far with WooCommerce', 'We\'d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2021-11-26 09:33:40', NULL, 0, 'plain', '', 0, 'info'),
(31, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2021-11-26 09:33:41', NULL, 0, 'plain', '', 0, 'info'),
(32, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2021-11-26 09:33:41', NULL, 0, 'plain', '', 0, 'info'),
(33, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'Learn more about variable products', 'Variable products are a powerful product type that lets you offer a set of variations on a product, with control over prices, stock, image and more for each variation. They can be used for a product like a shirt, where you can offer a large, medium and small and in different colors.', '{}', 'unactioned', 'woocommerce-admin', '2021-11-26 09:55:01', NULL, 0, 'plain', '', 0, 'info'),
(34, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2021-11-29 14:03:54', NULL, 0, 'plain', '', 0, 'info'),
